import { BillingRequestDto, BillingResponseDto } from '../dto/billing.dto';

/**
 * Genera un UUID v4 simple
 */
function generateUUID(): string {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
    const r = (Math.random() * 16) | 0;
    const v = c === 'x' ? r : (r & 0x3) | 0x8;
    return v.toString(16);
  });
}

/**
 * Genera la fecha en formato YYYYMMDD
 */
function generateDate(): string {
  const now = new Date();
  const year = now.getFullYear();
  const month = String(now.getMonth() + 1).padStart(2, '0');
  const day = String(now.getDate()).padStart(2, '0');
  return `${year}${month}${day}`;
}

/**
 * Construye la respuesta de billing basada en el request
 */
export function buildBillingResponse(
  request: BillingRequestDto,
): BillingResponseDto {
  // Generar ID de orden Ãºnico
  const orderId = generateUUID();
  const checkoutId = generateUUID();
  const date = generateDate();

  // Base64 mock de PDF (mismo que se usaba antes)
  const base64Pdf =
    '';

  return {
    date,
    order: {
      id: orderId,
      state: 'CREADO',
    },
    state: 'PAID',
    store: request.store,
    base64: base64Pdf,
    device: request.device,
    terminal: 300,
    idCheckout: checkoutId,
    transaction: 3667,
    referenceNiubiz: request.paymentDatails.referenceNiubiz,
  };
}
